--This file contains code for item tables.

set serveroutput on;

/* drop table item;
drop table item_reviews;
drop table item_inventory; */

--Item Table creation
declare 
v_cnt number;
begin
    select count(*) into v_cnt from user_tables where table_name = upper('item');
    if v_cnt = 0 then
        execute immediate('
            create table item (
            item_id int generated by DEFAULT as IDENTITY (start with 1) not null primary key,
            item_name varchar(200),
            item_description varchar(500),
            item_status number(1) default 0,
            item_price number (8,2),
            item_update_timestamp timestamp default systimestamp,
            item_group number)');
        dbms_output.put_line('Item table created');
    else
        execute immediate('delete from item');
        commit;
        dbms_output.put_line('Item table already exists; deleted existing data and inserting again');
    end if;
end;
/
grant select on item to customer_owner;
grant select on item to order_owner;
grant select on item to customer_owner;
grant select on item to order_owner;
grant references on item to customer_owner, order_owner;
       
-- Item Review Table creation
declare 
v_cnt number;
begin
    select count(*) into v_cnt from user_tables where table_name = upper('item_reviews');
    if v_cnt = 0 then
        execute immediate('
            create table item_reviews (
            review_id int generated by DEFAULT as IDENTITY (start with 1) not null primary key,
            customer_id number,
            item_id number,
            FOREIGN KEY(item_id) REFERENCES item(item_id),
            FOREIGN KEY(customer_id) REFERENCES customer_owner.customer(customer_id),
            record_create_timestamp timestamp default systimestamp,
            review_desc varchar(2000), 
            review_rating number(1) not null,
            constraint check_rating check (review_rating>=1 and review_rating<=5),
            customer_purchase_item_flag number(1))');
        dbms_output.put_line('Item_reviews table created');
    else
        execute immediate('delete from item_reviews');
        commit;
        dbms_output.put_line('Item_reviews table already exists; deleted existing data and inserting again');
    end if;
end;
/

grant select, update, delete, insert on item_reviews to customer_owner;

--Item Inventory Table creation
declare 
v_cnt number;
begin
    select count(*) into v_cnt from user_tables where table_name = upper('item_inventory');
    if v_cnt = 0 then
        execute immediate('
            create table item_inventory (
            inventory_id int generated by DEFAULT as IDENTITY (start with 1) not null primary key,
            item_id number,
            FOREIGN KEY(item_id) REFERENCES item(item_id),
            item_quantity number default 0,
            supplier_id number default 36,
            supplier_name varchar(30) default ''internal'',
            item_group number,
            inventory_update_timestamp timestamp default systimestamp
            )');
        dbms_output.put_line('Item_inventory table created');
    else
        execute immediate('delete from item_inventory');
        dbms_output.put_line('Item_inventory table already exists; deleted existing data and inserting again');
    end if;
end;
/

grant select, update on item_inventory to order_owner;

--Codes table creation
declare 
v_cnt number;
begin
    select count(*) into v_cnt from user_tables where table_name = upper('codes');
    if v_cnt = 0 then
        execute immediate('
            create table codes(
            code_column varchar(1),
            code_name varchar(40),
            code_description varchar(200),
            entity_name varchar(120))');
        dbms_output.put_line('Codes table created');
    else
        execute immediate('delete from codes');
        dbms_output.put_line('Codes table already exists; deleted existing data and inserting again');
    end if;
end;
/
grant select on codes to order_owner, customer_owner;

