INSERT INTO ITEM_REVIEWS (CUSTOMER_ID, ITEM_ID, RECORD_CREATE_TIMESTAMP, REVIEW_DESC, REVIEW_RATING, CUSTOMER_PURCHASE_ITEM_FLAG) 
VALUES (20, 4, to_timestamp('2016-02-05 00:00:00.000', 'YYYY-MM-DD HH24:MI:SS.FF'), 'When I opened the package and felt the heft of this speaker, I was sure this was going to be a winner. After pairing it with my Ipod and playing the first song, the disappointment set in. The sound quality was not as clear or crisp as the Bose or UE Megablast. It may be a good party speaker with better sound at higher volumes, but it just didn''t pass my sound test at normal volumes.', 5, 0);
