/*drop table order_desc;
drop table order_payment_history;
drop table order_item_details; */

set serveroutput on;

declare 
v_cnt number;
begin
    select count(*) into v_cnt from user_tables where table_name = upper('order_desc');
    if v_cnt = 0 then
        execute immediate('
            create table order_desc (
            order_id int generated by default as identity(start with 1) not null primary key,
            order_creation_timestamp timestamp default systimestamp,
            order_update_timestamp timestamp default systimestamp,
            customer_id number,
            FOREIGN KEY(customer_id) REFERENCES customer_owner.customer(customer_id),
            order_status number(1),
            order_return_flag number(1),
            total_amount_usd number (8,2),
            shipping_address_id number,
            FOREIGN KEY(shipping_address_id) REFERENCES customer_owner.customer_address(address_id),
            orig_order_id number)');
        dbms_output.put_line('Order Description table created');
        
    else
        execute immediate('delete from order_desc');
        commit;
        dbms_output.put_line('Order desc table already exists; deleted existing data and inserting again');
    end if;
end;
/

grant select, update, insert on order_desc to customer_owner;
grant select on order_desc to item_owner;


declare 
v_cnt number;
begin
    select count(*) into v_cnt from user_tables where table_name = upper('order_payment_history');
    if v_cnt = 0 then
        execute immediate('
            create table order_payment_history (
            token_id int generated by default as identity(start with 1) not null primary key,
            order_id number,
            FOREIGN KEY(order_id) REFERENCES order_desc(order_id),
            payment_status number(1), 
            record_create_timestamp timestamp default systimestamp,
            payment_type varchar(50),
            amount_received number (8,2),
            billing_address_id number,
            FOREIGN KEY(billing_address_id) REFERENCES customer_owner.customer_address(address_id))');
        dbms_output.put_line('Order Payment History table created');
        
    else
        execute immediate('delete from order_payment_history');
        commit;
        dbms_output.put_line('Order payment history table already exists; deleted existing data and inserting again');
    end if;
end;
/

grant select, update on order_payment_history to customer_owner;
grant select on order_payment_history to item_owner;

declare 
v_cnt number;
begin
    select count(*) into v_cnt from user_tables where table_name = upper('order_item_details');
    if v_cnt = 0 then
        execute immediate('
            create table order_item_details (
            order_item_id int generated by default as identity(start with 1) not null primary key,
            item_id number,
            FOREIGN KEY(item_id) REFERENCES item_owner.item(item_id),
            order_id number,
            FOREIGN KEY(order_id) REFERENCES order_desc(order_id),
            inventory_update_status number(1),
            is_item_returned number(1),
            record_create_timestamp timestamp default systimestamp,
            record_update_timestamp timestamp default systimestamp)');
        dbms_output.put_line('Order Item Details table created');
        
    else
        execute immediate('delete from order_item_details');
        commit;
        dbms_output.put_line('Order item details table already exists; deleted existing data and inserting again');
    end if;
end;
/

grant select on order_item_details to customer_owner;
grant select, update on order_item_details to item_owner;

